'use strict';

var exports = module.exports;

function stringUtils() {
    return {
        'isString': function (item) {
            return typeof item === 'string';
        }
    };
}

exports.isUsable = function (item) {
    var usable;
    var requirements = Array.prototype.slice.call(arguments, 1);

    usable = !(
        item === undefined ||
        item === null
        );

    if (usable) {
        requirements.forEach(function (req) {
            if (req === undefined || req === null) {
                return;
            }

            var fullReq = req.toLowerCase();
            if (fullReq.indexOf('as_string') === 0) {
                usable = stringUtils().isString(item);
            }

            return usable;
        });
    }

    return usable;
};